  INSERT INTO daily_horoscopes (date, type, type_value, overall_score, love_score, career_score, wealth_score, health_score, overall_content, love_content, career_content, wealth_content, health_content, lucky_color, lucky_number, lucky_direction, status) VALUES
  (base_date + day_offset, 'zodiac', '白羊座', 5, 4, 5, 4, 5, '今日运势极佳，充满活力和斗志，适合开展新计划。贵人运强，容易得到他人帮助。', '单身者有机会在社交场合遇到心仪对象；恋爱中的情侣感情升温，适合表白或进一步发展关系。', '工作效率高，创意十足，提出的方案容易获得上司认可。适合主动争取新项目或晋升机会。', '正财运佳，工资奖金可能有意外之喜。投资方面需谨慎，不宜冒进，稳健为上。', '精力充沛，体能状态极佳。适合进行户外运动，但要注意安全防护，避免意外受伤。', '红色', '9', '正东方有利于财运，东南方有利于感情发展。', 'published'),
  (base_date + day_offset, 'zodiac', '金牛座', 3, 3, 4, 5, 3, '运势平稳，适合稳扎稳打。财运不错，但需注意健康问题。保持耐心，好事自然来。', '感情进展缓慢但稳定，不要急于求成。已婚者要多关心伴侣，倾听对方心声。', '工作按部就班，虽无惊喜但也不会出错。坚持做好本职工作，积累经验为未来铺路。', '理财能力强，收入稳定。可考虑稳健型投资，如基金定投。偏财运一般，不建议投机。', '注意肠胃健康，饮食要规律清淡。久坐上班族要多活动，预防颈椎问题。', '绿色', '6', '北方利于工作，西方利于财运积累。', 'published'),
  (base_date + day_offset, 'zodiac', '双子座', 4, 5, 4, 3, 4, '沟通运势极佳，人际关系活跃。适合拓展社交圈，多参与聚会活动，会有意外收获。', '桃花运旺盛，魅力四射。单身者脱单机会大增，恋爱中的要注意与异性保持适当距离。', '适合开展需要沟通协调的工作，谈判、销售、策划都能取得好成绩。团队合作效率高。', '花销较大，尤其是社交娱乐方面。建议适度控制，避免冲动消费。正财稳定，偏财运一般。', '状态良好，但要注意睡眠质量。避免熬夜，保持规律作息。适当补充维生素。', '黄色', '3', '南方利于社交，东方利于学习进修。', 'published'),
  (base_date + day_offset, 'zodiac', '巨蟹座', 4, 5, 3, 4, 4, '情感运势上扬，家庭温馨和睦。直觉敏锐，凭感觉做决定往往是对的。适合处理家庭事务。', '家庭型星座今日格外重视亲密关系。伴侣间相处融洽，单身者可能从朋友发展为恋人。', '工作中依赖直觉和经验，虽然效率不是最高但准确率高。适合处理人际关系问题。', '收支平衡，无大进账也无大支出。可考虑为家庭做一些长期规划，如房产、保险等。', '情绪稳定对健康有利。注意保护胃部，避免过冷过热的食物。女性注意妇科保养。', '白色', '2', '西南方有利于家庭和谐，北方利于财运。', 'published'),
  (base_date + day_offset, 'zodiac', '狮子座', 5, 4, 5, 4, 4, '自信满满，领导能力凸显。今日是展现才华的好时机，大胆表现自己，必有收获。', '魅力值爆表，容易成为焦点。单身者追求者众多，恋爱中的要给对方足够的安全感。', '工作表现出色，容易得到领导赏识。适合主持会议、带领团队。你的决策很有说服力。', '收入增加，可能有额外奖金或红包。适合考虑大额投资，但仍需理性分析，切忌盲目。', '体力充沛，但不要过度劳累。注意心脏健康，避免情绪过于激动。适度运动保持状态。', '金色', '8', '南方利于事业发展，东方利于健康运动。', 'published'),
  (base_date + day_offset, 'zodiac', '处女座', 4, 3, 5, 4, 3, '注重细节的一天，适合做需要精确计算和规划的事情。工作效率高，但要注意休息。', '感情上过于挑剔可能错失良缘。学会包容对方的小缺点，完美的人是不存在的。', '工作能力强，细致认真。容易在项目中发现问题并提出解决方案，获得同事认可。', '理财意识强，善于记账和规划。今日适合整理财务状况，制定长期理财计划。', '容易因工作压力大而感到疲惫。注意放松心情，避免过度焦虑。肠胃需要养护。', '蓝色', '5', '西北方利于工作，东南方利于学习。', 'published'),
  (base_date + day_offset, 'zodiac', '天秤座', 4, 5, 4, 3, 4, '人际和谐，社交运佳。适合参加聚会活动，可能遇到对你有帮助的贵人。保持优雅风度。', '感情运势极佳，单身者有望遇到理想对象。已有伴侣的相处愉快，适合约会增进感情。', '工作中善于协调各方关系，团队氛围融洽。适合做中介、调解等需要平衡能力的工作。', '花销有所增加，主要在社交和形象打造方面。适度即可，避免为面子过度消费。', '身心愉悦，状态良好。但要避免久坐，多活动筋骨。注意保持良好的坐姿和站姿。', '粉色', '7', '西方利于社交恋爱，北方利于合作谈判。', 'published'),
  (base_date + day_offset, 'zodiac', '天蝎座', 5, 4, 5, 5, 4, '洞察力惊人，直觉准确。今日适合做重大决策，深度思考能发现事物本质。运势强劲。', '感情深沉，对伴侣的占有欲较强。要学会给对方空间，信任是感情的基础。', '工作中展现出强大的专注力和执行力。适合处理复杂棘手的问题，你的分析能力无人能及。', '财运极佳，投资眼光准确。可考虑进行中长期投资，但要做好风险评估。偏财运也不错。', '精力旺盛，但要注意情绪管理。避免过度压抑，适当释放压力。多喝水促进代谢。', '深紫色', '4', '北方利于事业，东方利于财运。', 'published'),
  (base_date + day_offset, 'zodiac', '射手座', 5, 4, 4, 4, 5, '乐观向上，充满探索精神。今日适合学习新知识、尝试新事物。运动运强，出行运佳。', '感情自由洒脱，不喜欢被束缚。单身者可能在旅行或学习中遇到有趣的人。', '工作中创意十足，思维活跃。适合做企划、市场推广等需要创新的工作。敢于突破常规。', '财运平稳，适合学习投资理财知识。旅行支出可能较多，建议提前做好预算。', '体能极佳，精力充沛。适合户外运动和旅行。注意安全，避免运动损伤。', '紫色', '9', '远方利于拓展，东南方利于学习。', 'published'),
  (base_date + day_offset, 'zodiac', '摩羯座', 4, 3, 5, 5, 3, '踏实稳重，责任心强。今日适合处理严肃重要的事务。事业运佳，财运稳定上升。', '感情上比较保守传统，需要时间来培养感情。不要太死板，适当浪漫会让感情升温。', '工作态度认真，执行力强。领导信任你，会把重要任务交给你。脚踏实地必有回报。', '理财稳健，收入稳定增长。适合做长期投资规划。对金钱有良好的掌控能力。', '注意骨骼和关节保养，避免过度劳累。工作繁忙也要注意休息，保证充足睡眠。', '棕色', '8', '北方利于事业，西方利于理财。', 'published'),
  (base_date + day_offset, 'zodiac', '水瓶座', 5, 4, 4, 3, 4, '思维独特，创意无限。今日适合头脑风暴，你的想法容易得到认可。人际关系活跃。', '对待感情理性客观，需要精神上的共鸣。单身者可能被有才华的人吸引。', '工作中善于创新，提出的方案往往能让人眼前一亮。适合做需要创造力的工作。', '花销较随性，建议做好财务规划。可能会在科技产品或学习上有所支出。', '身体状况良好，但要注意腿部和循环系统。多活动，避免久坐久站。', '银色', '1', '东北方利于创意，南方利于社交。', 'published'),
  (base_date + day_offset, 'zodiac', '双鱼座', 4, 5, 3, 4, 4, '直觉敏锐，想象力丰富。今日容易获得灵感，适合从事艺术创作。感情运势极佳。', '浪漫多情，感情细腻。单身者桃花运旺，恋爱中的甜蜜温馨。要注意区分现实和幻想。', '工作中发挥想象力和同理心，适合做设计、策划、咨询等工作。倾听他人意见能提升效率。', '财运平稳，但要避免因心软而借钱给他人。投资需理性分析，不要被情绪左右。', '情绪影响健康，保持心情愉悦很重要。注意足部保养，选择舒适的鞋子。睡眠质量要保证。', '海蓝色', '7', '西方利于艺术创作，南方利于感情发展。', 'published');
